function addEvent(e,t,n){e.addEventListener?e.addEventListener(t,n):e.attachEvent("on"+t,n)}function removeEvent(e,t,n){e.removeEventListener?e.removeEventListener(t,n):e.detachEvent("on"+t,n)}function getByClass(e){if(document.getElementsByClassName)return document.getElementsByClassName(e);for(var t=document.getElementsByTagName("*"),n=[],o=0;o<t.length;o++)for(var r=t[o].className.split(" "),a=0;a<r.length;a++)if(r[a]==e){n.push(t[o]);break}return n}function getStyle(e,t){return window.getComputedStyle?getComputedStyle(e,null)[t]:e.currentStyle[t]}function setCookie(e,t,n){var o=new Date;o.setDate(o.getDate()+n),document.cookie=e+"="+t+";expires="+o+";path=/"}function getCookie(e){for(var t=document.cookie.split("; "),n=0;n<t.length;n++){var o=t[n].split("=");if(o[0]===e)return o[1]}}function removeCookie(e){setCookie(e,1,-1)}